
// IE8+
//
// Snippet from:
//

function removeClass(/* Element */ el, className) {
  if (el.classList)
    el.classList.remove(className);
  else
    el.className = el.className
      .replace(new RegExp('(^|\\b)' + className.split(' ').join('|') + '(\\b|$)', 'gi'), ' ');
}

module.exports = removeClass;